mapper.update:
UPDATE collection
SET update_time = now(),
    duration = CASE
                   WHEN (#{duration} * #{rate} >= 0) THEN duration + #{duration} * #{rate}
                   WHEN (duration >= ABS(#{duration} * #{rate})) THEN duration + #{duration} * #{rate}
                   ELSE 0
        END
WHERE id = #{collectionId}
